import Foundation

final class DataStore {
    
}

extension DataStore {
    static let shared = DataStore()
}

extension DataStore {
    // Remove once API implemented
    func generations(completion: @escaping (Result<[Generation]>) -> Void) {
        DispatchQueue.global().asyncAfter(deadline: .now() + 1.0) {
         completion(.success(Generation.generations))
//            completion(.failure(URLError(.badServerResponse)))
        }
    }
    
    // all verrsions for particular generation
    func versions(generation: Generation, completion: @escaping (Result<[Version]>) -> Void) {
        DispatchQueue.global().asyncAfter(deadline: .now() + 1.0) {
            let versions = Version.versions.filter({ $0.model.generation.name == generation.name })
            // filter where the model.generation is equal to the one we pass in
            completion(.success(versions))
        }
    }
    
    // method that fetches array of specifications if successful or an error if fails.
    func specifications(completion: @escaping (Result<[Specification]>) -> Void) {
        // after now +1s do something
        DispatchQueue.global().asyncAfter(deadline: .now() + 1.0) {
            completion(.success(Specification.specifications))
        }
    }
    
    func metrics(version: Version, completion: @escaping (Result<[Metric]>) -> Void) {
        DispatchQueue.global().asyncAfter(deadline: .now() + 1.0) {
            let metrics = Metric.metrics.filter({ $0.specification.version.name == version.name })
            // filter where the model.generation is equal to the one we pass in
            completion(.success(metrics))
        }
    }
    
}

// everything below here will be deleted when we have an external data store
// convenience functions to search data for the sake of find methods below

fileprivate extension Generation {
    static func find(name: String) -> Generation {
        return Generation.generations.first(where: { $0.name == name })!
    }
}

fileprivate extension Model {
    static func find(name: String) -> Model {
        return Model.models.first(where: { $0.name == name })!
    }
}

fileprivate extension Version {
    static func find(name: String) -> Version {
        return Version.versions.first(where: { $0.name == name })!
    }
}

fileprivate extension Specification {
    static func find(name: String) -> Specification {
        return Specification.specifications.first(where: { $0.name == name })!
    }
}

